import React from "react";
import { Text, View, TextInput, SafeAreaView, Button } from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import styled from "styled-components/native";

const Container = styled.View`
  flex: 1;
  background-color: white;
  align-items: center;
  justify-content: center;
`;
const Wrapper = styled.SafeAreaView`
  width: 100%;
  padding: 15px;
`;
const Info = styled.Text`
  font-size: 18px;
  color: blue;
  font-weight: 500;
  margin-bottom: 10px;
`;
const Input = styled.TextInput`
  font-size: 18px;
  color: blue;
  font-weight: 500;
  border: 1px solid black;
  margin-bottom: 10px;
  border-radius: 15px;
`;

export default function App() {
  const [number, setNumber] = React.useState([]);
  const [response, setResponse] = React.useState("");

  const storeData = async (value) => {
    console.log(number);
    number.map((n) => {
      if (value === n) {
        setError("ასეთი ციფრი უკვე არსებობს");
      } else {
        setNumber((number) => [...number, value]);
      }
    });

    await AsyncStorage.setItem("@input", JSON.stringify(number));
  };
  const getData = (value) => {
    value > 100
      ? setResponse("შეიყვანეთ 1-დან 100-მდე ციფრი")
      : setResponse("");
    setNumber(value);
  };
  React.useEffect(async () => {
    const value = await AsyncStorage.getItem("@input");
    if (number !== null) {
      setNumber(value);
    } else {
      setNumber("");
    }
  }, []);
  return (
    <Container>
      <Wrapper>
        <Info>{response}</Info>
        <Info>{number}</Info>
        <Input
          onChangeText={(n) => getData(n)}
          placeholder="შეიყვანეთ 1-დან 100-მდე ციფრი"
          keyboardType="numeric"
        />
        <Button title="Press me" onPress={() => storeData(number)} />
      </Wrapper>
    </Container>
  );
}
